import React, { Component } from "react";

import Card from "@material-ui/core/Card";
import CardActionArea from "@material-ui/core/CardActionArea";
import CardContent from "@material-ui/core/CardContent";
import CardMedia from "@material-ui/core/CardMedia";
import CardActions from "@material-ui/core/CardActions";
import Typography from "@material-ui/core/Typography";
import Box from "@material-ui/core/Box";
import Popover from "@material-ui/core/Popover";
import PopupState, { bindTrigger, bindPopover } from "material-ui-popup-state";
import {
  MDBContainer,
  MDBBtn,
  MDBModal,
  MDBModalBody,
  MDBModalHeader,
  MDBModalFooter
} from "mdbreact";

import "./item.css";
import { ADD_ITEM } from "../../redux/actions";
import { connect } from "react-redux";

class Item extends Component {
  constructor(props) {
    super(props);
    this.state = {
      modal: false
    };
  }
  toggle = () => {
    this.setState({
      modal: !this.state.modal
    });
  };

  render() {
    return (
      <Card className="item">
        <CardActionArea>
          <CardMedia
            className="cardMedia"
            image={this.props.item.image}
            onClick={this.toggle}
          />

          <CardContent>
            <div className="itemName">{this.props.item.name}</div>
            <div className="price">Price: ${this.props.item.price}</div>
          </CardContent>
        </CardActionArea>
        <CardActions
          className="addItem"
          onClick={() => {
            this.props.dispatch(ADD_ITEM(this.props.item));
          }}
        >
          <div>Add to Cart</div>
        </CardActions>
        {/* <MDBContainer>
          <MDBModal isOpen={this.state.modal} toggle={this.toggle} centered style={{}}>
            <MDBModalHeader toggle={this.toggle}>MDBModal title</MDBModalHeader>
            <MDBModalBody> (...)</MDBModalBody>
            <MDBModalFooter>
              <MDBBtn color="secondary" onClick={this.toggle}>
                Close
              </MDBBtn>
              <MDBBtn color="primary">Save changes</MDBBtn>
            </MDBModalFooter>
          </MDBModal>
        </MDBContainer> */}
        <CardContent className="addItem">
          <PopupState variant="popover" popupId="demo-popup-popover">
            {popupState => (
              <div>
                <div {...bindTrigger(popupState)}>Details</div>
                <Popover
                  {...bindPopover(popupState)}
                  anchorOrigin={{
                    vertical: "bottom",
                    horizontal: "center"
                  }}
                  transformOrigin={{
                    vertical: "top",
                    horizontal: "center"
                  }}
                >
                  <Box p={2}>
                    <Typography>
                      Description: {this.props.item.description}
                    </Typography>
                  </Box>
                </Popover>
              </div>
            )}
          </PopupState>
        </CardContent>
      </Card>
    );
  }
}

export default connect()(Item);
