import React, { Component } from "react";
import Button from "@material-ui/core/Button";
import "semantic-ui-css/semantic.min.css";
import { withRouter } from "react-router-dom";
import { Grid, Header, Icon, Message } from "semantic-ui-react";
import "./AccountOrder.css";

class AccountOrders extends Component {
  constructor(props) {
    super(props);
    this.state = {
      userId: null,
      firstName: "default",
      lastName: "default",
      email: "default",
      fail: false
    };
  }

  getUserOrder() {
    console.log(this.state.userId);
    fetch("http://localhost:3001/api/orders/", {
      method: "GET",
      credentials: "include",
      headers: {
        userId: this.state.userId
      }
    })
      .then(response => response.json())
      .then(json => {
        console.log(json);
      });
  }
  componentDidMount() {
    // console.log(this.props.match.params.profileId);
    fetch("http://localhost:3001/api/profile", {
      method: "GET",
      credentials: "include"
    })
      .then(response => response.json())
      .then(json => {
        if (json === null) {
          //   this.props.history.push("/login");
        } else {
          this.setState({
            userId: json._id,
            firstName: json.firstName,
            lastName: json.lastName,
            email: json.email
          });
        }
      });
  }

  render() {
    this.getUserOrder();

    console.log(this.state);
    return (
      <React.Fragment>
        <Header />
        <div className="Content">
          <div className="Card">
            <p className="CardText">My Orders</p>

            {this.state.ordersList.map(order => {
              return (
                <div className="Order">
                  {/* <div className="OrderHeader">
                                        <a href="#">{order._id}</a>
                                    </div> */}
                  <div>
                    {order.order.map(item => (
                      <div
                        key={item._id}
                        style={{
                          display: "flex",
                          alignItems: "center",
                          margin: "5px 0",
                          borderBottom: "1px solid #cecece"
                        }}
                      >
                        <div
                          style={{
                            width: "80px",
                            height: "80px",
                            overflow: "hidden",
                            position: "relative"
                          }}
                          className="ImageContainer"
                        >
                          <img
                            style={{
                              maxWidth: "100%",
                              maxHeight: "100%",
                              position: "absolute",
                              left: "50%",
                              transform: "translateX(-50%)"
                            }}
                            src={item.product.productPic[0].img}
                          />
                        </div>
                        <div>
                          <p className="odtitle">{item.product.name}</p>
                          <div
                            style={{
                              fontSize: "14px",
                              color: "#555",
                              fontWeight: "bold"
                            }}
                          >
                            <p>Quantity: {item.quantity}</p>
                            <p>${item.price * item.quantity}</p>
                          </div>
                        </div>
                      </div>
                    ))}
                  </div>
                  <div className="OrderFooter">
                    <p>
                      Ordered On <span>{this.formatDate(order.orderDate)}</span>
                    </p>
                    <p>
                      Order Total <span>${this.getOrderTotal(order._id)}</span>
                    </p>
                  </div>
                </div>
              );
            })}
          </div>
        </div>
      </React.Fragment>
    );
  }
}

export default AccountOrders;
