import React, { Component } from "react";

import Card from "@material-ui/core/Card";
import CardActionArea from "@material-ui/core/CardActionArea";
import CardContent from "@material-ui/core/CardContent";
import CardMedia from "@material-ui/core/CardMedia";
import CardActions from "@material-ui/core/CardActions";

import "./item.css";
import { ADD_ITEM } from "../../redux/actions";
import { connect } from "react-redux";

class Item extends Component {
  constructor(props) {
    super(props);
    this.state = {
      isPremium: false
    };
  }
  componentDidMount() {
    // console.log(this.props.match.params.profileId);
    fetch("http://localhost:3001/api/profile", {
      method: "GET",
      credentials: "include"
    })
      .then(response => response.json())
      .then(json => {
        if (json === null) {
          // this.props.history.push("/login");
        } else {
          this.setState({
            isPremium: json.isPremium
          });
        }
      });
  }
  render() {
    return (
      <Card className="item">
        <CardActionArea>
          <CardMedia
            className="cardMedia"
            image="data:image/png;base64, {this.props.item.image}"
          />
          <CardContent>
            <div className="itemName">{this.props.item.name}</div>
            {!this.state.isPremium ? (
              <div className="price">Price: ${this.props.item.price}</div>
            ) : (
              <div className="price">Price: ${this.props.item.price * 0.5}</div>
            )}
          </CardContent>
        </CardActionArea>
        <CardActions
          className="addItem"
          onClick={() => {
            this.props.dispatch(ADD_ITEM(this.props.item));
          }}
        >
          <div>Add to Cart</div>
        </CardActions>
      </Card>
    );
  }
}

export default connect()(Item);
