import React from "react";

import "./UserTable.scss";
import Search from "@material-ui/icons/Search";
import SaveAlt from "@material-ui/icons/SaveAlt";
import ChevronLeft from "@material-ui/icons/ChevronLeft";
import ChevronRight from "@material-ui/icons/ChevronRight";
import FirstPage from "@material-ui/icons/FirstPage";
import LastPage from "@material-ui/icons/LastPage";
import Check from "@material-ui/icons/Check";
import FilterList from "@material-ui/icons/FilterList";
import Remove from "@material-ui/icons/Remove";

import MaterialTable from "material-table";
import { Button } from "@material-ui/core";

const Row = ({ id, firstName, lastName, email, remove }) => (
  <div className="row">
    <div className="remove">
      <a href="#" onClick={() => remove(id)}>
        X
      </a>
    </div>
    <div>{id}</div>
    <div>{firstName}</div>
    <div>{lastName}</div>
    <div>{email}</div>
  </div>
);

class UserTable extends React.Component {
  state = {
    data: [
      {
        id: 403,
        firstName: "Task 403",
        lastName: "High",
        email: "Improvement"
      },
      {
        id: 403,
        firstName: "Task 403",
        lastName: "High",
        email: "Improvement"
      },
      {
        id: 403,
        firstName: "Task 403",
        lastName: "High",
        email: "Improvement"
      }
    ]
  };

  /* 
     I like to write it this way to explicity state that a function is being returned
     But you could simplify this by using arrow syntax twice,

     compareBy = (key) => (a,b) => { ...... }
  */
  compareBy = key => {
    return function(a, b) {
      if (a[key] < b[key]) return -1;
      if (a[key] > b[key]) return 1;
      return 0;
    };
  };

  sortBy = key => {
    let arrayCopy = [...this.state.data];
    arrayCopy.sort(this.compareBy(key));
    this.setState({ data: arrayCopy });
  };

  remove = rowId => {
    // Array.prototype.filter returns new array
    // so we aren't mutating state here
    const arrayCopy = this.state.data.filter(row => row.id !== rowId);
    this.setState({ data: arrayCopy });
  };

  render() {
    const rows = this.state.data.map(rowData => (
      <Row remove={this.remove} {...rowData} />
    ));

    return (
      <div className="table">
        <div className="header">
          <div className="remove"></div>
          <div onClick={() => this.sortBy("id")}>ID</div>
          <div onClick={() => this.sortBy("firstName")}>Title</div>
          <div onClick={() => this.sortBy("lastName")}>Priority</div>
          <div onClick={() => this.sortBy("email")}>Priority</div>
        </div>
        <div className="body">{rows}</div>
      </div>
    );
  }
}

export default UserTable;
